#ifndef ACCOUNT_HXX
#define ACCOUNT_HXX

#include <string>
using namespace std;

class Account
{
    public:

        Account()
        {
            myAccountNumber = 0;
            myBalance = 0.0;
        }
        Account(double initial)
        {
            myAccountNumber = 0;
            myBalance = initial;
        }

        double getBalance()
        {
            return (myBalance);
        }
        int getAccountNumber() const
        {
            return (myAccountNumber);
        }

        void setAccountNumber(int num)
        {
            myAccountNumber = num;
        }
        void setPassword(const char* password)
        {
            myPassword = password;
        }
        const char* getPassword()
        {
            return (myPassword.data());
        }   

        double deposit(double amount);

        double debit(double amount);

    private:

        int myAccountNumber;
        double myBalance;
        string myPassword;
};

#endif // ACCOUNT_HXX

#ifndef BASE_DISPLAY_HXX
#define BASE_DISPLAY_HXX

class BaseDisplay
{

    public:

        BaseDisplay() {};
        ~BaseDisplay() {};

        virtual void showInfoToUser(const char* message);
        virtual void showBalance(double balance);
};

#endif // BASE_DISPLAY_HXX

#include "Account.hxx"

double Account::deposit(double amount)
{
    myBalance += amount;
    return (getBalance());
}


double Account::debit(double amount)
{
    myBalance -= amount;
    return (getBalance());
}

#include "Bank.hxx"
#include "Account.hxx"

Bank::Bank() : myAccounts()
{
	myCurrentAccountNumber = 0;
}

Bank::~Bank()
{
    // NYI: Clean up account list
}

// Get acount number. Only return valid object if password is correct
Account* Bank::getAccount(int num, string password)
{
    Account* userAccount = NULL;
    if (myAccounts.size() > num)
    {
        userAccount = (Account*)myAccounts[num];
    }
    if ((userAccount != NULL) && (password.compare(userAccount->getPassword()) != 0))
    {
        // account wrong if account number does not match
        userAccount = NULL;
    }
    // No account with this number/password exists!!!
    return NULL;

}

// Create a new account and return a reference to it
Account* Bank::addAccount()
{
    Account* userAccount = new Account();
    userAccount->setAccountNumber(myCurrentAccountNumber++);
    myAccounts.push_back(userAccount);
    return userAccount;
}

#include "ATM.hxx"
#include "BaseDisplay.hxx"

ATM::ATM(Bank* bank, BaseDisplay* display)
{
    myBank = bank;
    myDisplay = display;
}

void ATM::viewAccount(int accountNumber, string password)
{
    if ( !(myCurrentAccount = myBank->getAccount(accountNumber, password)) )
    {
        myDisplay->showInfoToUser("Invalid account");
    }
}

void ATM::fillUserRequest(UserRequest request, double amount)
{
    if (myCurrentAccount)
        switch (request)
        {
            case REQUEST_BALANCE:
                showBalance(); break;
            case REQUEST_DEPOSIT:
                makeDeposit(amount); break;
            case REQUEST_WITHDRAW:
                withdraw(amount); break;
        }
}

void ATM::showBalance()
{
    double bal = myCurrentAccount->getBalance();
    myDisplay->showInfoToUser("Current Balance");
    myDisplay->showBalance(bal);
}

void ATM::makeDeposit(double amount)
{
    double bal = myCurrentAccount->deposit(amount);
    myDisplay->showInfoToUser("Updated Balance");
    myDisplay->showBalance(bal);
}

void ATM::withdraw(double amount)
{
    double bal = myCurrentAccount->deposit(amount * -1.0);
    myDisplay->showInfoToUser("Updated Balance");
    myDisplay->showBalance(bal);
}
